//import logo from './logo.svg';
import logo from './max.jpg';
import './App.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import UofTResources from './UofTResources';


function App() {
  const [animateHome, setAnimateHome] = useState(false);
  const [animateProjects, setAnimateProjects] = useState(false);

  const handleHomeClick = () => {
    setAnimateHome(true);
    setAnimateProjects(false);
    setTimeout(() => {
      console.log("Animation completed.");
    }, 500);
  };

  const handleProjectsClick = () => {
    setAnimateHome(false);
    setAnimateProjects(true);
    setTimeout(() => {
      console.log("Animation completed.");
    }, 500);
  };


  //Above are constants and functions for animation!! Duh!

  return (
    <div className="App">

      <nav className="App-nav">
        {/* Apply animation class to Home button based on state */}
        <a href="#home" onClick={handleHomeClick} className={`nav-item ${animateHome ? 'slide-out-right' : ''}`}>Home</a>
        {/* Apply animation class to Projects button based on state */}
        <a href="#projects" onClick={handleProjectsClick} className={`nav-item ${animateProjects ? 'slide-out-left' : ''}`}>TODO: Projects</a>
        <a href="#resources" className="nav-item">UofT Resources</a>
        <a href="https://www.overleaf.com/read/xyvycpwfxnxj" target="_blank" rel="noopener noreferrer" className="nav-item">Resume</a>
      </nav>

      <div className={`content ${animateHome ? 'slide-in-right' : ''} ${animateProjects ? 'slide-in-left' : ''}`}>
        <Routes>
          <Route path="/uoft-resources" element={<UofTResources />} />
        </Routes>

        {/* Conditionally render Home component */}
        {animateHome && <Home />}
      </div>

    </div>
  );
}


function Home() {
  return (
    <header className="App-header">
      <img src={logo} className="App-logo" alt="logo" />
      <p>
        Edit <code>src/App.js</code> and save to reload.
      </p>
      <a
        className="App-link"
        href="https://reactjs.org"
        target="_blank"
        rel="noopener noreferrer"
      >
        Learn React
      </a>
    </header>
  );
}



export default App;
