import React from 'react';
import maxPicture from '../images/max.jpg'

export default function Home() {
    const pictureStyle = {
        display: 'block',
        'margin-left': 'auto',
        'margin-right': 'auto',
        marginTop: '50px',
        height: 'auto',
        width: '40%',
        border: '5px solid #e4ddca',
        borderImage: 'linear-gradient(to bottom, #e4ddca, transparent) 1 49%'
      };

    const textStyle =  {
        color: 'white',
        'text-align': 'center'
    }

// Above is CSS code

    return (
        <>
            <div style={{marginTop: '20px'}}></div> {/* This is just for padding */}

            <h1 style={{fontSize: '75px', ...textStyle}}>
                I am Maksym Woychyshyn.
            </h1>

            <img src={maxPicture} className="App-logo" alt="logo" style={pictureStyle} />

            <h2 style={textStyle}>
                I code. Perferably not on paper.
            </h2>

            <h2 style={{
  width: '100%', // default width
  paddingLeft: '0', // default padding left
  paddingTop: '3rem', // padding top
  paddingBottom: '3rem', // padding bottom
  '@media (min-width: 768px)': { // medium and larger viewports
    width: '50%', // width for col-md-6 and col-lg-6
    paddingLeft: '3rem', // padding left for pl-md-5
  }
}}>
  I code. Preferably not on paper.
</h2>

        </>
    )
}
