//import logo from './logo.svg';
import logo from './max.jpg';
import './App.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import UofTResources from './UofTResources';


function App() {
  const [animate, setAnimate] = useState(false);

  const handleProjectsClick = () => {
    setAnimate(true);
    setTimeout(() => {
      // Additional actions after animation, like routing
      console.log("Animation completed.");
    }, 500); // This duration should match the CSS animation duration
  };


  //Above are constants and functions for animation!! Duh!

  return (
    <div className="App">

      <nav className="App-nav">
        <a href="#home" className="nav-item">Home</a>
        <a href="#projects" onClick={handleProjectsClick} className="nav-item">TODO: Projects</a>
        <a href="#resources" className="nav-item">UofT Resources</a>
        <a href="https://www.overleaf.com/read/xyvycpwfxnxj" target="_blank" rel="noopener noreferrer" className="nav-item">Resume</a>
      </nav>

      <div className={`content ${animate ? 'slide-out-left' : ''}`}>
        <Routes>
          <Route path="/uoft-resources" element={<UofTResources />} />
        </Routes>

        {/* Call Home function to render the home content */}
        <Home />
      </div>

    </div>
  );
}


function Home() {
  return (
    <header className="App-header">
      <img src={logo} className="App-logo" alt="logo" />
      <p>
        Edit <code>src/App.js</code> and save to reload.
      </p>
      <a
        className="App-link"
        href="https://reactjs.org"
        target="_blank"
        rel="noopener noreferrer"
      >
        Learn React
      </a>
    </header>
  );
}



export default App;
