import React from 'react';
import Home from './pages/Home';

function App() {

  const appStyle = {
    background: "linear-gradient(to bottom, #282c34 30%, #0398fc 150%)",
    height: '100vh',
    width: '100vw',
    overflow: 'auto'
  };

  const navStyle = {
    color: 'white',
    padding: '10px 20px',
    display: 'flex',
    justifyContent: 'space-around',
    position: 'fixed',
    top: 0,
    width: '100%',
    zIndex: 1000
  };

  const navItemStyle = {
    color: 'white',
    'text-decoration': 'none', /* Removes underline from links */
    padding: '10px', /* Adds space around each link */
    'font-size': '16px',
    ':hover': { /* Hover styles */
    backgroundColor: '#61dafb', /* Light blue background on hover */
    color: '#282c34' /* Dark text on hover */
  }
  }

  //Above are CSS styles

  return (
    <>


    <div style={appStyle}>
      <nav style={navStyle}>
              <a href="#home" style={navItemStyle}>Home</a>
              <a href="https://www.overleaf.com/read/xyvycpwfxnxj" target="_blank" rel="noopener noreferrer" style={navItemStyle}>Resume</a>
      </nav>

      <Home />
    </div>
    </>
  );
}

export default App;
