import React from 'react';
import Home from './pages/Home';

function App() {

  const appStyle = {
    background: "linear-gradient(to bottom, #282c34 30%, #0398fc 150%)",
    height: '100vh',
    width: '100vw',
    overflow: 'auto'
  };

  const navStyle = {
    color: 'white',
    padding: '10px 20px',
    display: 'flex',
    justifyContent: 'space-around',
    position: 'fixed',
    top: 0,
    width: '100%',
    zIndex: 1000
  };

  const navItemStyle = {
    color: 'white',
    textDecoration: 'none', /* Removes underline from links */
    padding: '10px', /* Adds space around each link */
    fontSize: '16px',
    backgroundColor: 'transparent' /* Initial background color */
  };

  //Above are CSS styles. Below are functions.
  function handleHover() {
    this.setState({ hover: true });
  }

  // Function to handle mouse leave
  function handleMouseLeave() {
    this.setState({ hover: false });
  }

  return (
    <>


    <div style={appStyle}>
      <nav style={navStyle}>
              <a href="#home" style={navItemStyle} onMouseEnter={handleHover} onMouseLeave={handleMouseLeave}>Home</a>
              <a href="https://www.overleaf.com/read/xyvycpwfxnxj" target="_blank" rel="noopener noreferrer" style={navItemStyle}>Resume</a>
      </nav>

      <Home />
    </div>
    </>
  );
}

export default App;
